(function(){tinymce.create("tinymce.plugins.footnote",{init:function(a,b){a.addButton("footnote",{title:PB_FootnotesToken.fn_title,image:b+"/../images/fn.png",onclick:function(){var c=a.selection.getContent();if(c!=""){var d=c;a.selection.setContent("[footnote]"+d+"[/footnote]")}else{var d=prompt("Footnote Content","Enter your footnote content here.");if(d!=""){a.execCommand("mceInsertContent",false,"[footnote]"+d+"[/footnote]")}}}})},createControl:function(b,a){return null}});tinymce.PluginManager.add("footnote",tinymce.plugins.footnote)})();